PersonObject person1 = PersonObject.makePerson() .displayName("Joe") .set("foo","bar") .set("bar","baz") .get();
PersonObject person2 = person1.<PersonObject,PersonBuilder>template( ASBase.withoutFields("foo","bar")) .set("bar","xyz") .set("baz",123) .get();
Difference diff = person1.diff(person2);
System.out.println(diff);

for (Pair<String,Pair<Object,Object>> change : diff.changed()) {
  String field = change.first();
  Pair<Object,Object> values = change.second();
  Object origValue = values.first();
  Object newValue = values.second();
  System.out.println(
    String.format(
      "Field '%s' changed from '%s' to '%s'",
      field,
      origValue,
      newValue));
}
